import React, { useEffect, useState } from 'react';
import styles from './WhatsappVNFeature.css';
import mojs from 'mo-js';

// const initialState = {
// 	isClicked: false,
// };

const useVNAnimation = () => {
	const [animationTimeline, setAnimationTimeline] = useState(
		() => new mojs.Timeline({ repeat: 999 })
	);
	useEffect(() => {
		const tlDuration = 600;
		const padlockBtn = new mojs.Html({
			el: '#open-padlock',
			duration: tlDuration,

			y: { 0: -15 },

			speed: 0.3,
			// delay: 300,
		}).then({
			y: 0,
			speed: 0.3,
			// repeat: 999,
		});

		const ArrowUp = new mojs.Html({
			el: '#arrow-up',
			duration: tlDuration,
			y: { 0: -15 },
			// delay: 500,
			easing: 'ease.inout',
			// repeat: 999,

			speed: 0.3,
		}).then({
			y: 0,
			speed: 0.3,
		});
		const TextSlide = new mojs.Html({
			el: '#slide',
			duration: tlDuration,
			opacity: { 1: 0.3 },
			// delay: 500,
			x: { 0: -7 },
			// repeat: 999,

			speed: 0.3,
		}).then({
			opacity: { 0.3: 1 },
			speed: 0.1,
			x: 0,
		});
		const LeftIconAnim = new mojs.Html({
			el: '#left-icon',
			duration: tlDuration,
			opacity: { 1: 0.4 },
			// delay: 500,

			// repeat: 999,

			speed: 0.3,
		}).then({
			opacity: { 0.5: 1 },
			speed: 0.3,
		});
		const FilledMicAnim = new mojs.Html({
			el: '#filled-mic',
			duration: tlDuration,
			opacity: { 1: 0.2 },
			// delay: 500,

			// repeat: 999,

			speed: 0.3,
		}).then({
			opacity: { 0.2: 1 },
			speed: 0.3,
		});
		const newAnimationTimeline = animationTimeline.add(
			padlockBtn,
			ArrowUp,
			TextSlide,
			LeftIconAnim,
			FilledMicAnim
		);
		setAnimationTimeline(newAnimationTimeline);
	}, []);

	return animationTimeline;
};

const pad = num => {
	return num.toString().length > 1 ? num : '0' + num;
};
function WhatsappVNFeature() {
	const [seconds, setSeconds] = useState(0);
	const [isClicked, setIsClicked] = useState(false);
	// const [clickState, setClickState] = useState(initialState);
	// const { isClicked } = clickState;

	const animationTimeline = useVNAnimation();
	const handleVNClick = () => {
		animationTimeline.replay();

		setIsClicked(isClicked => true);
		console.log(isClicked);
		setInterval(() => {
			setSeconds(seconds => seconds + 1);
		}, 1000);

		// setSeconds(seconds => seconds + 1);
		// setClickState(prevState => ({
		// 	isClicked: true,
		// }));
	};

	return (
		<>
			<button>
				<div className='left-section'>
					<FilledMicrophone />
					<TimeCount seconds={seconds} className='timeClass' />

					<Text />
					<LeftIcon className='less' />
				</div>

				<div className='mic' onClick={handleVNClick}>
					<MicIcon />
				</div>

				<div className='top-section'>
					<OpenpadlockIcon />
					<ArrowUpIcon />
				</div>
			</button>
		</>
	);
}

export default WhatsappVNFeature;

